PRAGMA foreign_keys=OFF;
BEGIN TRANSACTION;
CREATE TABLE android_metadata (locale TEXT);
CREATE TABLE `users` (`name` TEXT NOT NULL, `surname` TEXT NOT NULL, `dateOfBirth` TEXT NOT NULL, `pin` TEXT NOT NULL, `clientCode` TEXT NOT NULL, PRIMARY KEY(`clientCode`));
CREATE TABLE `operations` (`id` INTEGER PRIMARY KEY AUTOINCREMENT NOT NULL, `clientCode` TEXT NOT NULL, `dateO` TEXT NOT NULL, `dateV` TEXT NOT NULL, `description` TEXT NOT NULL, `amount` REAL NOT NULL, `operationType` TEXT NOT NULL, `bankAccount` TEXT NOT NULL, `cardCode` TEXT,`iban` TEXT NOT NULL,`recipient` TEXT ,FOREIGN KEY(`clientCode`) REFERENCES `users`(`clientCode`) ON UPDATE CASCADE ON DELETE NO ACTION , FOREIGN KEY(`bankAccount`) REFERENCES `conti`(`codConto`) ON UPDATE CASCADE ON DELETE NO ACTION ,FOREIGN KEY(`iban`) REFERENCES `conti`(`iban`) ON UPDATE CASCADE );
CREATE TABLE `conti` (`codConto` TEXT NOT NULL, `codCliente` TEXT NOT NULL, `saldo` REAL NOT NULL, `iban` TEXT NOT NULL, `stato` TEXT NOT NULL, PRIMARY KEY(`codConto`), FOREIGN KEY(`codCliente`) REFERENCES `users`(`clientCode`) ON UPDATE CASCADE ON DELETE NO ACTION );
CREATE TABLE `carte` (`nrCarta` INTEGER NOT NULL, `dataScadenza` TEXT NOT NULL, `cvc` TEXT NOT NULL, `codUtente` TEXT NOT NULL, `codConto` TEXT NOT NULL, `saldo` REAL NOT NULL, `statoCarta` TEXT NOT NULL, PRIMARY KEY(`nrCarta`), FOREIGN KEY(`codUtente`) REFERENCES `users`(`clientCode`) ON UPDATE CASCADE ON DELETE NO ACTION , FOREIGN KEY(`codConto`) REFERENCES `conti`(`codConto`) ON UPDATE CASCADE ON DELETE NO ACTION );
CREATE TABLE room_master_table (id INTEGER PRIMARY KEY,identity_hash TEXT);
DELETE FROM sqlite_sequence;
CREATE INDEX `index_operations_clientCode` ON `operations` (`clientCode`);
COMMIT;